import { shallowMount } from '@vue/test-utils'
import mdbAlert from '@/components/Components/Alert.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbAlert)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbAlert, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('is close button rendered & emits close event correctly', () => {
    const wrapper = shallowMount(mdbAlert, {
      propsData: {
        dismiss: true
      }
    })
    expect(wrapper.contains('button')).toBe(true)
    expect(wrapper.contains('span')).toBe(true)
    expect(wrapper.find('span').text()).toBe('Ã—')
    wrapper.find('span').trigger('click')
    expect(wrapper.emitted().closeAlert).toBeTruthy()
  })

})