import { shallowMount } from '@vue/test-utils'
import mdbBadge from '@/components/Components/Badge.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbBadge)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('has classes "badge" & "primary-color" if color "primary" is set', () => {
    const wrapper = shallowMount(mdbBadge, {
      propsData: {
        color: 'primary'
      }
    })
    expect(wrapper.classes()).toContain('badge')
    expect(wrapper.classes()).toContain('primary-color')
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbBadge, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })
})