import { shallowMount } from '@vue/test-utils'
import mdbBtnToolbar from '@/components/Components/ButtonToolbar.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbBtnToolbar)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbBtnToolbar, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are classes correct', () => {
    const wrapper = shallowMount(mdbBtnToolbar)
    expect(wrapper.classes()).toContain('btn-toolbar')
  })

})