import { shallowMount } from '@vue/test-utils'
import mdbCard from '@/components/Components/Card.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbCard)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbCard, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are classes correct', () => {
    const wrapper = shallowMount(mdbCard, {
      propsData: {
        reverse: true
      }
    })
    expect(wrapper.classes()).toContain('card')
    expect(wrapper.classes()).toContain('card-cascade')
    expect(wrapper.classes()).toContain('wider')
    expect(wrapper.classes()).toContain('reverse')
  })
})