import { shallowMount } from '@vue/test-utils'
import mdbCardAvatar from '@/components/Components/CardAvatar.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbCardAvatar)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbCardAvatar, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are classes correct', () => {
    const wrapper = shallowMount(mdbCardAvatar)
    expect(wrapper.classes()).toContain('avatar')
  })
})