import { shallowMount } from '@vue/test-utils'
import mdbCardBody from '@/components/Components/CardBody.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbCardBody)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbCardBody, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are classes correct', () => {
    const wrapper = shallowMount(mdbCardBody, {
      propsData: {
        color: 'primary',
        cascade: true
      }
    })
    expect(wrapper.classes()).toContain('card-body') 
    expect(wrapper.classes()).toContain('primary-color') 
    expect(wrapper.classes()).toContain('card-body-cascade') 
  })
})