import { shallowMount } from '@vue/test-utils'
import mdbCardFooter from '@/components/Components/CardFooter.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbCardFooter)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbCardFooter, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are classes correct', () => {
    const wrapper = shallowMount(mdbCardFooter, {
      propsData: {
        color: 'primary',
        textColor: 'white'
      }
    })
    expect(wrapper.classes()).toContain('card-footer') 
    expect(wrapper.classes()).toContain('primary') 
    expect(wrapper.classes()).toContain('white-text') 
  })
})