import { shallowMount } from '@vue/test-utils'
import { mdbCardTitle } from '@/components/Components/CardTitle.vue'

describe('Component', () => {
    test('is a Vue instance', () => {
        const wrapper = shallowMount(mdbCardTitle)
        expect(wrapper.isVueInstance()).toBeTruthy()
    })

    test('is slot correct', () => {
        const wrapper = shallowMount(mdbCardTitle, {
            slots: {
                default: 'Test'
            }
        })
        expect(wrapper.text()).toBe('Test')
    })

    test('are classes correct', () => {
        const wrapper = shallowMount(mdbCardTitle)
        expect(wrapper.classes()).toContain('card-title') 
    })

    test('is tag correct', () => {
        const wrapper = shallowMount(mdbCardTitle, {
            propsData: {
                tag: 'h3'
            }
        })
        expect(wrapper.is('h3')).toBe(true)
    })
})