import { shallowMount } from '@vue/test-utils'
import { mdbCardUp } from '@/components/Components/CardUp.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbCardUp)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbCardUp, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag & classes correct', () => {
    const wrapper = shallowMount(mdbCardUp, {
      propsData: {
        tag: 'p',
        color: 'primary',
        gradient: 'peach'
      }
    })
    expect(wrapper.is('p')).toBe(true) 
    expect(wrapper.classes()).toContain('card-up') 
    expect(wrapper.classes()).toContain('primary-color') 
    expect(wrapper.classes()).toContain('peach-gradient') 
  })
})