import { shallowMount } from '@vue/test-utils'
import { mdbEdgeHeader } from '@/components/Components/EdgeHeader.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbEdgeHeader)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbEdgeHeader, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag & classes correct', () => {
    const wrapper = shallowMount(mdbEdgeHeader, {
      propsData: {
        tag: 'p',
        color: 'primary'
      }
    })
    expect(wrapper.is('p')).toBe(true)
    expect(wrapper.classes()).toContain('edge-header')
    expect(wrapper.classes()).toContain('primary')
  })
})