import { shallowMount } from '@vue/test-utils'
import { mdbJumbotron } from '@/components/Components/Jumbotron.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbJumbotron)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbJumbotron, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag & classes correct', () => {
    const wrapper = shallowMount(mdbJumbotron, {
      propsData: {
        tag: 'p',
        fluid: true
      }
    })
    expect(wrapper.is('p')).toBe(true)
    expect(wrapper.classes()).toContain('jumbotron')
    expect(wrapper.classes()).toContain('jumbotron-fluid')
  })
})