import { shallowMount } from '@vue/test-utils'
import { mdbListGroup } from '@/components/Components/ListGroup.vue'
import { mdbListGroupItem } from '@/components/Components/ListGroupItem.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbListGroup)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbListGroup, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag & classes correct', () => {
    const wrapper = shallowMount(mdbListGroup, {
      propsData: {
        tag: 'ul',
        horizontal: true,
        md: true
      }
    })
    expect(wrapper.is('ul')).toBe(true)
    expect(wrapper.classes()).toContain('list-group')
    expect(wrapper.classes()).toContain('list-group-horizontal-md')
  })

  test('is list group item rendered correct', () => {
    const wrapper = shallowMount(mdbListGroup, {
      slots: {
        default: [mdbListGroupItem]
      }
    })
    expect(wrapper.contains('.list-group-item')).toBe(true)
  })
})