import { shallowMount, mount } from '@vue/test-utils'
import { mdbProgress } from '@/components/Components/Progress.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbProgress)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbProgress, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag, classes & attributes correct', () => {
    const min = 20
    const max = 120
    const value = 50
    const expectedProgress = '30%'
    const wrapper = mount(mdbProgress, {
      propsData: {
        tag: 'div',
        min,
        max,
        value,
      }
    })
    expect(wrapper.is('div')).toBe(true)
    expect(wrapper.classes()).toContain('progress')
    expect(wrapper.contains('.progress-bar')).toBe(true)
    expect(wrapper.find('.progress-bar').attributes().style).toBe(`width: ${expectedProgress};`)
    expect(wrapper.find('.progress-bar').attributes('aria-valuenow')).toBe(`${value}`)
  })

})