import { shallowMount } from '@vue/test-utils'
import { mdbTab } from '@/components/Components/Tab.vue'
import { mdbTabItem } from '@/components/Components/TabItem.vue'
import { mdbTabContent } from '@/components/Components/TabContent.vue'
import { mdbTabPane } from '@/components/Components/TabPane.vue'

describe('Component', () => {
  test('is a Vue instance', () => {
    const wrapper = shallowMount(mdbTab)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is slot correct', () => {
    const wrapper = shallowMount(mdbTab, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

  test('are tag, classes & children correct', () => {
    const wrapper = shallowMount(mdbTab, {
      propsData: {
        tag: 'ul',
      },
      slots: {
        default: mdbTabItem
      }
    })
    expect(wrapper.is('ul')).toBe(true)
    expect(wrapper.classes()).toContain('nav')
    expect(wrapper.contains('.nav-item')).toBe(true)
  })

  test('is tab content a Vue instance', () => {
    const wrapper = shallowMount(mdbTabContent)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is tab pane a Vue instance', () => {
    const wrapper = shallowMount(mdbTabPane)
    expect(wrapper.isVueInstance()).toBeTruthy()
  })

  test('is tab pane slot correct', () => {
    const wrapper = shallowMount(mdbTabPane, {
      slots: {
        default: 'Test'
      }
    })
    expect(wrapper.text()).toBe('Test')
  })

})